import React, {useState , useEffect} from 'react'
import Cart from "./components/Cart/Cart"
import NavBar from './components/NavBar/NavBar';
import {commerce} from './lib/commerce'
import Homepage from './components/Homepage/Homepage'
import Footer from './components/Footer/Footer'
import {
  BrowserRouter as Router,
  Switch,
  Route

} from "react-router-dom";
import Products from './components/Products/Products'
import SingleProduct from './components/SingleProduct/SingleProduct'


const App = () => {
    //eslint-disable-next-line
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState({})
    const fetchProductsPerCategory = async () => {
      const { data: products } = await commerce.products.list({ limit: 200 });
      const { data: categories } = await commerce.categories.list();
      const productsPerCategory = categories.reduce((acc, category) => {
        return [
          ...acc,
          {
            ...category,
            productsData: products.filter((product) =>
              product.categories.find((cat) => cat.id === category.id)
            ),
          },
        ];
      }, []);

      setCategories(productsPerCategory);
    };


    const fetchProducts = async () => {
        const { data } = await commerce.products.list();

        setProducts(data);
      };

      const fetchCart = async () => {
        setCart(await commerce.cart.retrieve());
      };
      //eslint-disable-next-line
    const aggiungiAlCarrello=  async (productId, quantity) =>{
        const item = await commerce.cart.add(productId, quantity);
        setCart(item.cart);
    }

    const svuotaCarrello = async () => {
        const response = await commerce.cart.empty();    
        setCart(response.cart);
      };

      const aggiornaCarrello = async (productId, quantity) => {
        const {cart} = await commerce.cart.update(productId, {quantity});
        setCart(cart);
      };

      const rimuoviElemento = async (productId) =>{
        const {cart} = await commerce.cart.remove(productId);
        setCart(cart);
      }



    useEffect(() => {
        fetchProducts();
        fetchCart();
    },[]);



    commerce.categories.list().then(categories => console.log(categories.data));


    return(
      <Router>

         <NavBar totalItems={cart.total_items}/>
          <Switch>
            <Route exact path="/">
              <Homepage/>
              <h1>Prodotti:</h1>
              <Products categories={categories} products={products} onAddToCart={aggiungiAlCarrello}/>
            </Route>

            <Route exact path="/products">
              <Products  products={products} onAddToCart={aggiungiAlCarrello}/>
            </Route>

            <Route exact path="/cart">
              <Cart cart={cart} empty={svuotaCarrello} update={aggiornaCarrello} remove={rimuoviElemento} />
            </Route>

            <Route exact path="/product-view/:id">
              <SingleProduct onAddToCart={aggiungiAlCarrello}/>
            </Route>

           </Switch> 
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>
           <br/>

            <Footer/>

        </Router>
    )
}

export default App;
