import React from 'react'
import {Container, Typography, Button, Grid} from '@material-ui/core';
import useStyles from './styles';

const Cart = ({cart, len}) => {

    const classes=useStyles();
    const CarrelloVuoto=()=>{
        <Typography variant="subtitle1">Il tuo carrello Ã¨ vuoto, inizia a comprare</Typography>
    }
    const CarrelloPieno = () => (
        <>
          <Grid container spacing={3}>
            {cart.line_items.map((item) => (
              <Grid item xs={12} sm={4} key={item.id}>

              </Grid>
            ))}
          </Grid>
          <div className={classes.cardDetails}>
            <Typography variant="h4">Totale: {cart.subtotal.formatted_with_symbol}</Typography>
            <div>
              <Button className={classes.emptyButton} size="large" type="button" variant="contained" color="secondary" >Svuota il carrello</Button>
              <Button className={classes.checkoutButton}  size="large" type="button" variant="contained" color="primary">Checkout</Button>
            </div>
          </div>
        </>
      );

      return (
        <Container>
          <div className={classes.toolbar} />
          <Typography className={classes.title} variant="h3" gutterBottom>Your Shopping Cart</Typography>
          { len ? <CarrelloPieno/> : <CarrelloVuoto/> }
        </Container>
      );
    }

export default Cart
